import React from 'react';
import { Fade, Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import HomePic from './images/cover.jpg'

const spanStyle = {
  padding: '20px',
  background: '#efefef',
  color: '#000000'
}

const divStyle = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundSize: 'cover',
  height: '400px'
}
const slideImages = [
  {
    url: HomePic ,
    caption: 'Slide 1'
  },
  {
    url: HomePic,
    caption: 'Slide 2'
  },
  {
    url: HomePic,
    caption: 'Slide 3'
  },
  {
    url: HomePic,
    caption: 'Slide 4'
  },
];

const Slideshow = () => {
    return (
        <div className="slide-container">
          <Fade autoplay={true} >
            {slideImages.map((fadeImage, index) => (
              <div key={index}>
                <img style={{ width: '100%' , borderRadius:'20px', backgroundRepeat: "no-repeat", backgroundSize: "cover",backgroundPosition: "center",display: 'flex',}} src={fadeImage.url} />
                <h2>{fadeImage.caption}</h2>
              </div>
            ))}
          </Fade>
        </div>
      )
}

export default Slideshow
