import React from 'react';
import { Fade, Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import HomePic from './images/cover.jpg'
import { Container, Stack, Typography } from '@mui/material';

const slideImages = [
  {
    url: HomePic,
    caption: 'Slide 1',
    text: 'Text for Slide 1'
  },
  {
    url: HomePic,
    caption: 'Slide 2',
    text: 'Text for Slide 2'
  },
  {
    url: HomePic,
    caption: 'Slide 3',
    text: 'Text for Slide 3'
  },
  {
    url: HomePic,
    caption: 'Slide 4',
    text: 'Text for Slide 4'
  },
];

const Slideshow = () => {
  return (
    <div className="slide-container">
      <Fade autoplay={true} >
        {slideImages.map((slide, index) => (
          <div key={index}>
            <img style={{ width: '100%', height: 'auto' }} src={slide.url} />
            <Container maxWidth="lg">
              <Stack spacing={2}>
                <div style={{ backgroundColor: 'rgba(241, 86, 42, 0.3)', width: '70%', margin: '0 auto', padding: '20px', borderRadius: '30px' }}>
                  <div>
                    <div style={{ color: 'white' }}>
                      <Typography><strong>{slide.text}</strong></Typography>
                    </div>
                  </div>
                </div>
              </Stack><br />
            </Container>
          </div>
        ))}
      </Fade>
    </div>
  )
}

export default Slideshow;
