import React from 'react';
import { Fade } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import HomePic from './images/cover.jpg';
import { Container, Stack, Typography } from '@mui/material';

const slideImages = [
  {
    url: HomePic,
    caption: 'Slide 1',
    text: (
        <div>
        <Typography variant="h4" sx={{ fontFamily: 'Arial', fontWeight: 'bold' }}>
          Our youth of <span style={{ color: '#2c99db' }}>Pakistan</span> are the face of Pakistan
        </Typography>
        <Typography className='title_nav'><b>Village Area</b></Typography>
      </div>
      )
  },
  {
    url: HomePic,
    caption: 'Slide 2',
    text: 'Text for Slide 2'
  },
  {
    url: HomePic,
    caption: 'Slide 3',
    text: 'Text for Slide 3'
  },
  {
    url: HomePic,
    caption: 'Slide 4',
    text: 'Text for Slide 4'
  },
];

const textStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  textAlign: 'left',
  color: 'white',
  zIndex: 1,
  backgroundColor: 'rgba(241, 86, 42, 0.3)',
  width: '70%',
  margin: '0 auto', 
  padding: '20px' , 
  borderRadius: '30px'
  // Additional styles can be added here
};

const Slideshow = () => {
  return (
    <div className="slide-container" style={{ position: 'relative' }}>
      <Fade autoplay={true}>
        {slideImages.map((slide, index) => (
          <div key={index} style={{ position: 'relative' }}>
            <img style={{ width: '100%', height: '600px' , borderRadius:'30px'}} src={slide.url} />
            <div style={textStyle}>
              <Typography variant="h4" sx={{ fontFamily: 'Arial', fontWeight: 'bold',  }}>{slide.text}</Typography>
            </div>
          </div>
        ))}
      </Fade>
    </div>
  );
}

export default Slideshow;
