import React from 'react';
import { Fade, Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import HomePic from './images/cover.jpg'
import { Container, Stack, Typography } from '@mui/material';

const spanStyle = {
  padding: '20px',
  background: '#efefef',
  color: '#000000'
}

const divStyle = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  backgroundSize: 'cover',
  height: '400px'
}
const slideImages = [
  {
    url: HomePic ,
    caption: 'Slide 1'
  },
  {
    url: HomePic,
    caption: 'Slide 2'
  },
  {
    url: HomePic,
    caption: 'Slide 3'
  },
  {
    url: HomePic,
    caption: 'Slide 4'
  },
];

const containerStyles = {
    width: '100%', // Full width
    height: '600px', // Fixed height
    borderRadius: '30px', // Rounded corners
    display: 'flex', // Flex layout
    backgroundRepeat: 'no-repeat', // No repeating background
    backgroundSize: 'cover', // Cover background size
    backgroundPosition: 'center', // Centered background
    marginTop: '-0px'
  };

const Slideshow = () => {
    return (
        <div className="slide-container">
          <Fade autoplay={true} >
            {slideImages.map((fadeImage, index) => (
              <div key={index}>

                <Container maxWidth="lg" sx={{color:"#ffffff"}}>
                <img style={containerStyles} src={fadeImage.url} />
            <Stack spacing={2}>
            <div style={{ backgroundColor: 'rgba(241, 86, 42, 0.3)', width: '70%', margin: '0 auto', padding: '20px' , borderRadius: '30px'}}>
  <div>
    <div style={{ color: 'white' }}>
      <Typography><strong>Our youth of <span style={{color:'#2c99db'}}>Pakistan</span> are the face </strong></Typography>
      <Typography><b>of pakistan</b></Typography>
      <Typography className='title_nav'><b>Village Area</b></Typography>
    </div>
  </div>
</div>


             </Stack><br />
             </Container>
              </div>
            ))}
          </Fade>
        </div>
      )
}

export default Slideshow
