import React from 'react';
import { Fade } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import HomePic from './images/cover.jpg';
import { Container, Stack, Typography } from '@mui/material';

const slideImages = [
  {
    url: HomePic,
    caption: 'Slide 1',
    text: 'Text for Slide 1'
  },
  {
    url: HomePic,
    caption: 'Slide 2',
    text: 'Text for Slide 2'
  },
  {
    url: HomePic,
    caption: 'Slide 3',
    text: 'Text for Slide 3'
  },
  {
    url: HomePic,
    caption: 'Slide 4',
    text: 'Text for Slide 4'
  },
];

const Slideshow = () => {
  return (
    <div className="slide-container" style={{ position: 'relative' }}>
      <Fade autoplay={true}>
        {slideImages.map((slide, index) => (
          <div key={index} style={{ position: 'relative' }}>
            <img style={{ width: '100%', height: 'auto' }} src={slide.url} />
            <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', textAlign: 'center', color: 'white', zIndex: 2 }}>
              <Typography variant="h4">{slide.text}</Typography>
            </div>
          </div>
        ))}
      </Fade>
    </div>
  );
}

export default Slideshow;
